import UIKit

class SecondViewController: UIViewController {
    
    
    
    @IBOutlet weak var imageView: UIImageView!
    @IBOutlet weak var activityIndicator: UIActivityIndicatorView!
    
    
    fileprivate var imageURL: URL?
    fileprivate var image: UIImage?{
        get {
            return imageView.image
        }
        set {
            activityIndicator.stopAnimating()
            activityIndicator.isHidden = true
            imageView.image = newValue
            imageView.sizeToFit()
        }
        
    }
    
    override func viewDidLoad() {
        super.viewDidLoad()
        fetchImage()
        delay(3) {
            self.loginAlert()
        }
        
        
        
        
    }
    
    fileprivate func delay(_ delay: Int, closure: @escaping () ->()){
        DispatchQueue.main.asyncAfter(deadline: .now() + .seconds(delay)) {
            closure ()
        }
        
    }
    
    fileprivate func loginAlert() {
        let ac = UIAlertController(title: "зарегестрирован?", message: "Введите ваш логин и пароль ", preferredStyle: .alert)
        let okAction = UIAlertAction(title: "Ok", style: .default, handler: nil)
        let cancelAction = UIAlertAction(title: "отмена", style: .default, handler: nil)
    
        ac.addAction(okAction)
        ac.addAction(cancelAction)
        
        ac.addTextField { (usernameTF) in
            usernameTF.placeholder = "введтите логин"
        }
        
        ac.addTextField { (usernamePasswordTF) in
                  usernamePasswordTF.placeholder = "введтите логин"
            usernamePasswordTF.isSecureTextEntry = true
              }
    
        self.present(ac, animated: true, completion: nil)
    }


    fileprivate func fetchImage (){
        
        imageURL = URL(string: "data:image/jpeg;base64,")
        activityIndicator.isHidden = false
        activityIndicator.startAnimating()
        
        let queue = DispatchQueue.global(qos: .utility)
        queue.async {
            
            guard let url = self.imageURL, let imageData = try? Data (contentsOf: url) else { return}
            DispatchQueue.main.async {
                
                self.image = UIImage(data: imageData)
            }
        }
    }
}







